mapper.select:
select w.wp_id, w.wp_name, w.wp_startTime, w.wp_endTime, w.wp_content, w.wp_keypoint, w.wp_prospect_performance, w.wp_actual_performance, w.wp_remark, w.wp_createDate, w.wp_status, w.t_user_id, w.file_id, w.check_point, w.if_summary, u.name from rl_workplans w join t_user u on w.t_user_id = u.t_user_id
w.wp_name=#{wpName,jdbcType=VARCHAR},
w.wp_createDate>#{createDate1}
and w.wp_createDate<#{createDate2}
order by w.wp_createDate desc

mapper.update:
        update rl_workplans r
		join t_user t on r.t_user_id = t.t_user_id
		set r.check_point = #{checkPoint}
		where
		r.wp_id = #{wpId}

update rl_workplans
set wp_actual_performance = #{wpActualPerformance},if_summary = 1
where wp_id = #{wpId}
